document.addEventListener("alpine:init",()=>{Alpine.data("artistCarousel",()=>({observers:[],isPaused:!1,init(){let e=Array.from(this.$el.querySelectorAll(".marquee"));e.length&&(e.forEach(e=>{const s=new IntersectionObserver(s=>{s.forEach(s=>{s.isIntersecting?this.isPaused||e.classList.remove("paused"):e.classList.add("paused")})},{threshold:.1});s.observe(e),this.observers.push(s)}),document.addEventListener("visibilitychange",()=>{e.forEach(e=>{document.hidden?(e.classList.add("paused"),this.isPaused=!0):(e.classList.remove("paused"),this.isPaused=!1)})}))}}))});