document.addEventListener("alpine:init",()=>{Alpine.data("rotatingIcon",()=>({observer:null,isPaused:!1,init(){const e=this.$el.querySelector(".rotate-360");this.observer=new IntersectionObserver(s=>{s.forEach(s=>{s.isIntersecting?this.isPaused||e.classList.remove("paused"):e.classList.add("paused")})},{threshold:.1}),this.observer.observe(e),document.addEventListener("visibilitychange",()=>{document.hidden?(e.classList.add("paused"),this.isPaused=!0):(e.classList.remove("paused"),this.isPaused=!1)})}}))});