document.addEventListener("alpine:init",()=>{Alpine.data("tickets",()=>({init(){const e=document.querySelectorAll(".ticket-wrapper");e.forEach(e=>{const t=e.querySelector(".ticket");if(t.classList.contains("sold-out"))return;const o=t.querySelectorAll(".sheen");e.addEventListener("mousemove",n=>{const a=e.getBoundingClientRect(),i=e.clientWidth,r=e.clientHeight,s=n.clientX-a.left-i/2,c=n.clientY-a.top-r/2,l=s/(i/2)*4,u=-c/(r/2)*4;gsap.to(t,{duration:.3,rotateY:l,rotateX:u,scale:1.03,ease:"power1.out"}),gsap.to(o,{duration:.3,x:-s/i*50+"%",y:-c/r*50+"%",ease:"power1.out"})}),e.addEventListener("mouseleave",()=>{gsap.to(t,{duration:.6,rotateY:0,rotateX:0,scale:1,ease:"power2.out"}),gsap.to(o,{duration:.6,x:"0%",y:"0%",ease:"power2.out"})})})}}))});